def test_sample1():
    assert solve(1, [5], []) == 5

def test_sample2():
    assert solve(3, [2, 3, 4], [(0, 1), (1, 2)]) == 9

def test_sample3():
    assert solve(3, [3, 5, 7], []) == 7

def test_sample4():
    assert solve(3, [3, 2, 5], [(0, 2), (1, 2)]) == 8

def test_single_task():
    assert solve(1, [10], []) == 10

def test_two_parallel():
    assert solve(2, [5, 3], []) == 5

def test_two_sequential():
    assert solve(2, [5, 3], [(0, 1)]) == 8

def test_complex_dag():
    # タスク0,1 -> タスク2 -> タスク3,4 -> タスク5
    durations = [1, 2, 3, 4, 5, 6]
    deps = [(0, 2), (1, 2), (2, 3), (2, 4), (3, 5), (4, 5)]
    # パス: max(1,2)+3+max(4,5)+6 = 2+3+5+6 = 16
    assert solve(6, durations, deps) == 16

def test_fork_join():
    # タスク0 -> タスク1,2 -> タスク3
    durations = [1, 2, 3, 4]
    deps = [(0, 1), (0, 2), (1, 3), (2, 3)]
    # パス: 1+max(2,3)+4 = 1+3+4 = 8
    assert solve(4, durations, deps) == 8
