def test_sample1():
    s = "((a)(b(c)))(d)"
    result = solve(s)
    assert "root" in result[0] or "a" in result[0]
    assert len(result) >= 4

def test_single_node():
    s = "(a)"
    result = solve(s)
    assert len(result) >= 1

def test_nested():
    s = "(a(b))";
    result = solve(s)
    assert len(result) >= 2
