def test_sample1():
    root = {
        "tag": "div",
        "children": [
            {"tag": "p", "children": []},
            {
                "tag": "span",
                "children": [{"tag": "a", "children": []}]
            }
        ]
    }
    result = solve(root)
    assert "<div>" in result
    assert "<p></p>" in result
    assert "<span>" in result
    assert "<a></a>" in result
    assert result.endswith("</div>")

def test_single_node():
    root = {"tag": "div", "children": []}
    result = solve(root)
    assert result == "<div></div>"

def test_nested():
    root = {
        "tag": "a",
        "children": [
            {"tag": "b", "children": [{"tag": "c", "children": []}]}
        ]
    }
    result = solve(root)
    assert "<a>" in result and "</a>" in result
    assert "<b>" in result and "</b>" in result
    assert "<c></c>" in result
